    }

    @Override
    public MenuItem next() {
        MenuItem menuItem = list[position];
        position += 1;
        return menuItem;
    }

    @Override
    public boolean hasNext() {
        if (position >= list.length || list[position] == null) return false;
        else return true;
    }

    @Override
    public void remove() { // Î∞òÎìúÏãú Í∏∞Îä•ÏùÑ Ï†úÍ≥µÌïòÏßÄ ÏïäÏïÑÎèÑÎê® Í∑∏Î†áÎã§Î©¥ java.lang.UnsupportedOperationExceptionÏùÑ ÎçòÏßÄÎèÑÎ°ù ÌïòÎ©¥Îê®
        if (position <= 0) Throw new IllegalStateException("next()Í∞Ä ÌïúÎ≤àÎèÑ Ìò∏Ï∂úÎêòÏßÄ ÏïäÏùå.");
        if (list[position - 1] != null) {
            for (int i = position - 1; i < (list.length - 1); i++) {
                list[i] = list[i + 1];
            }
            list[list.length - 1] = null;
        }
    }
}

